package bazi

// NewLunarDate 新建一个农历日期
func NewLunarDate(nYear int, nMonth int, nDay int, nHour int, nMinute int, nSecond int, isLeap bool) *TLunarDate {

	pDate := &TLunarDate{
		nYear:              nYear,
		nMonth:             nMonth,
		nConventionalMonth: nMonth,
		nDay:               nDay,
		nHour:              nHour,
		nMinute:            nMinute,
		nSecond:            nSecond,
		isLeap:             isLeap,
	}

	pDate.genLeap(isLeap) // 闰月转变成第几月

	// 农历 描述的月  换成第几个月的月

	// 检查日期合法性
	if !pDate.GetDateIsValid() {
		return nil
	}

}

// TLunarDate 农历日期
type TLunarDate struct {
	nYear              int
	nMonth             int  // 月份,  顺序月, 不是传统月, 比如闰4月 这里是(第)5月  而有闰4月的5月这里是(第)6月
	nLeapMonth         int  // 闰月 0表示不闰
	nDay               int  // 日
	nHour              int  // 时
	nMinute            int  // 分
	nSecond            int  // 秒
	isLeap             bool // 是否是闰月
	nConventionalMonth int  // 传统的月份. 也就是我们平时所说的月份(没有闰月的时候等于 nMonth)
}

// GetDateIsValid 返回日期是否合法
func (self *TLunarDate) GetDateIsValid() bool {

	if self.nYear < 1800 || self.nYear > 2299 {
		return false // 1800 之前的年份已经严重不精确  2299 也一样
	}

	// 1月开始, 13月结束
	if self.nMonth < 1 || self.nMonth > 13 {
		return false
	}

	// 1号开始
	if self.nDay < 1 {
		return false
	}

	if self.nLeapMonth == 0 {
		// 没有闰月的话 nMonth 只能是 1 ~ 12
		if self.nMonth == 13 {
			return false
		}
	}

	// 获取每个月有多少天, 超过天数的话 日期非法
	if self.nDay > self.GetMonthDays(self.nYear, self.nMonth) {
		return false
	}

	return true
}

// genLeap 闰月改变成第几月
func (self *TLunarDate) genLeap(isLeap bool) {
	self.GetLeapMonth() // 获取闰月信息

	// 没有闰月, 不用改
	if self.nLeapMonth == 0 {
		return
	}

	// 有闰月
	// 闰月之前不变
	if self.nMonth < self.nLeapMonth {
		return
	}

	// 闰月时
	if self.nMonth == self.nLeapMonth {
		if isLeap {
			// 闰月是下一个月
			self.nMonth++
			return
		} else {
			return
		}
	}

	// 超过闰月月
	self.nMonth++

}

