// GetLeapMonth 获取闰月
func (self *TLunarDate) GetLeapMonth() int {
	if self.nYear < 1800 || self.nYear > 2299 {
		self.nLeapMonth = 0
		return 0
	}
	nLeapMonth := leapMonthList[self.nYear-1800]
	nLeapMonth >>= 13 // 移除掉12个农历大小月
	self.nLeapMonth = nLeapMonth & 0x0F
	return self.nLeapMonth
}
