// GetAllDays 获取距离公元原点的日数, 这里是农历来的年月日
func (m *TLunarDate) GetAllDays() int {
	// 目前只能计算1800年到2300年的天数, 精度不够高
	if m.nYear < 1800 || m.nYear >= 2300 {
		return 0
	}

	if m.nMonth < 1 || m.nMonth > 13 {
		return 0
	}

	return allDayList[m.nYear-1800][m.nMonth-1] + m.nDay

}

// Year 年
func (m *TLunarDate) Year() string {
	return GetChnCharFromYear(m.nYear)
}

// Month 月
func (m *TLunarDate) Month() string {
	strResult := ""
	if m.nConventionalMonth == m.nMonth-1 {
		strResult += "闰"
	}

	switch m.nConventionalMonth {
	case 1:
		strResult += "一月"
	case 2:
		strResult += "二月"
	case 3:
		strResult += "三月"
	case 4:
		strResult += "四月"
	case 5:
		strResult += "五月"
	case 6:
		strResult += "六月"
	case 7:
		strResult += "七月"
	case 8:
		strResult += "八月"
	case 9:
		strResult += "九月"
	case 10:
		strResult += "十月"
	case 11:
		strResult += "冬月"
	case 12:
		strResult += "腊月"
	}

	return strResult
}

// Day 日
func (m *TLunarDate) Day() string {
	switch m.nDay {
	case 1:
		return "初一"
	case 2:
		return "初二"
	case 3:
		return "初三"
	case 4:
		return "初四"
	case 5:
		return "初五"
	case 6:
		return "初六"
	case 7:
		return "初七"
	case 8:
		return "初八"
	case 9:
		return "初九"
	case 10:
		return "初十"

	case 11:
		return "十一"
	case 12:
		return "十二"
	case 13:
		return "十三"
	case 14:
		return "十四"
	case 15:
		return "十五"
	case 16:
		return "十六"
	case 17:
		return "十七"
	case 18:
		return "十八"
	case 19:
		return "十九"
	case 20:
		return "二十"

	case 21:
		return "廿一"
	case 22:
		return "廿二"
	case 23:
		return "廿三"
	case 24:
		return "廿四"
	case 25:
		return "廿五"
	case 26:
		return "廿六"
	case 27:
		return "廿七"
	case 28:
		return "廿八"
	case 29:
		return "廿九"
	case 30:
		return "三十"

	}
	return ""
}

// Hour 时
func (m *TLunarDate) Hour() string {
	switch m.nHour {
	case 23, 0:
		return "子时" // 子时——晚11点钟到凌晨1点钟；
	case 1, 2:
		return "丑时" // 丑时——1点钟至3点钟；
	case 3, 4:
		return "寅时" // 寅时——3点钟至5点钟；
	case 5, 6:
		return "卯时" // 卯时——5点钟至7点钟；
	case 7, 8:
		return "辰时" // 辰时——7点钟至9点钟；
	case 9, 10:
		return "巳时" // 巳时——9点钟至11点钟；
	case 11, 12:
		return "午时" // 午时——11点钟至下午1点钟；
	case 13, 14:
		return "未时" // 未时——13点钟至15点钟；
	case 15, 16:
		return "申时" // 申时——下午3点钟至5点钟；
	case 17, 18:
		return "酉时" // 酉时——下午5点钟至7点钟；
	case 19, 20:
		return "戌时" // 戌时——下午7点钟至晚9点钟；
	case 21, 22:
		return "亥时" // 亥时——晚9点钟至11点钟。
	}
	return ""
}

func (m *TLunarDate) String() string {
	strResult := `农历:`
	strResult += m.Year() + "年"
	strResult += m.Month()
	strResult += m.Day()
	strResult += m.Hour()

	return strResult
}

// ToLunarDate 转成农历年
func (m *TLunarDate) ToSolarDate() *TSolarDate {
	return NewSolarDateFrom64TimeStamp(m.Get64TimeStamp())
}
